import React, { useContext } from 'react';
import LogContext from '../ContextAPI/LogsAPI/LogContext';
import EditTech from './EditTech';

const Techlist = () => {
    const context = useContext(LogContext);
    const { techs, deleteTechs } = context;
    return (
        <div
            className='modal'
            id='tech-list'
            style={{ width: '50%', height: '50%' }}
        >
            <div className='modal-content'>
                <div className='row'>
                    <h4 className='left'>Technician List :</h4>
                </div>
                <div
                    className='collection with-header container'
                    style={{ width: '100%', height: '100%' }}
                >
                    {techs &&
                        techs.map(tech => (
                            <li
                                className='collection-header'
                                key={tech._id}
                                tech={tech}
                            >
                                <i className='material-icons left'>face</i>
                                <strong
                                    className='left'
                                    style={{
                                        fontWeight: 'bolder'
                                    }}
                                >
                                    <a
                                        onClick='#edit-tech'
                                        className='modal-trigger'
                                    >
                                        {' '}
                                        {tech.firstName + ' ' + tech.lastName}
                                    </a>
                                </strong>
                                <EditTech />
                                <a
                                    href='#!'
                                    onClick={() => {
                                        deleteTechs(tech);
                                    }}
                                    className='secondary-content'
                                >
                                    <i className='material-icons grey-text'>
                                        delete
                                    </i>
                                </a>

                                <br />
                            </li>
                        ))}
                </div>
            </div>
        </div>
    );
};

export default Techlist;
