import React, { useState } from 'react';
import M from 'materialize-css/dist/js/materialize.min';

const Edit = () => {
    const onChange = e => {
        setData({ ...data, [e.target.name]: e.target.value });
    };
    const onSubmit = () => {
        if (message === '' || tech === '') {
            M.toast({ html: 'Please enter message and tech' });
        } else {
            console.log('Done');
            setAttention(false);
            setMessage('');
            setTech('');
        }
    };
    const [data, setData] = useState({
        message: '',
        attention: false,
        tech: ''
    });
    const { tech, message, attention } = data;
    return (
        <div
            className='modal'
            style={{ width: '75%', height: '75%' }}
            id='edit-log-modal'
        >
            <div className='modal-content'>
                <h4>Edit System Log : </h4>
                <div className='row'>
                    <div className='input-field'>
                        <input
                            type='text'
                            value={message}
                            name='message'
                            onChange={onChange}
                        />
                        <label htmlFor='message' className='active'>
                            Log Message
                        </label>
                    </div>
                </div>
                <div className='row'>
                    <div className='input-field'>
                        <select
                            name='tech'
                            onChange={onChange}
                            className='browser-default'
                            value={tech}
                        >
                            <option value='' disabled>
                                Select Technician
                            </option>
                            <option value='John Doe'>John Doe</option>
                            <option value='Amy Jackson'>Amy Jackson</option>
                            <option value='Bradd Pitt'>Bradd Pitt</option>
                        </select>
                    </div>
                </div>
                <div className='row'>
                    <div className='input-field'>
                        <label>
                            <input
                                type='checkbox'
                                className='filled-in'
                                value={attention}
                                checked={attention}
                                name='attention'
                                onChange={e =>
                                    setData({
                                        attention: !attention
                                    })
                                }
                            />
                            <span>Attention Required </span>
                        </label>
                    </div>
                </div>
                <div className='modal-footer'>
                    <a
                        href='#!'
                        onClick={onSubmit}
                        className='modal-close waves-light blue waves-effect btn '
                    >
                        Enter
                    </a>
                </div>
            </div>
        </div>
    );
};

export default Edit;
