import {
    GET_LOG,
    ADD_LOG,
    DELETE_LOG,
    UPDATE_LOG,
    ERROR,
    SET_CURRENT,
    CLEAR_CURRENT,
    SET_LOADING,
    UPDATE_TECH,
    DELETE_TECH,
    SEARCH_LOG,
    ADD_TECH,
    GET_TECH,
    CLEAR_LOG
} from '../Types';

const LogReducer = (state, action) => {
    switch (action.type) {
        default:
            return state;
        case GET_LOG:
            return {
                ...state,
                logs: action.payload,
                loading: false
            };
        case ERROR:
            return {
                ...state,
                error: action.payload
            };
    }
};

export default LogReducer;
